import tw from 'twin.macro';
import styled from 'styled-components';

const NewChatLayout = styled.div`
    ${tw`bg-gray-100 shadow-lg outline-none focus:outline-none rounded-md px-5 pl-16 font-semibold py-2 inline-block relative`}
    top: 50%;
    transform: translateY(-50%);
    cursor: default;
    & button { 
        border-color: #E6E5EA;
        &:focus {
            z-index: 2;
            outline: none;
            box-shadow: 0 0 0 2px rgba(49, 130, 206, .2);
        }
    }
`;

const AddButton = styled.button`
    ${tw`absolute inset-y-0 left-0 flex items-center px-3 border-r`}
`;

const SvgButton = () => {
    return (
        <svg className="fill-current h-4 w-4 text-blue-700" id="Layer_1" style={{enableBackground: 'new 0 0 32 32'}} version="1.1" viewBox="0 0 32 32" width="32px" xmlns="http://www.w3.org/2000/svg" ><path d="M28,14H18V4c0-1.104-0.896-2-2-2s-2,0.896-2,2v10H4c-1.104,0-2,0.896-2,2s0.896,2,2,2h10v10c0,1.104,0.896,2,2,2  s2-0.896,2-2V18h10c1.104,0,2-0.896,2-2S29.104,14,28,14z"></path></svg>
    );
}

// (e) => alert(e)
const NewChat = ({onClick}) => {
    return (
        <NewChatLayout type="text">
            New Conversation
            <AddButton onClick={onClick}>
                <SvgButton/>
            </AddButton>
        </NewChatLayout>
    );
}




export default NewChat;
