import React from 'react';

// Styles
import ChatLayout from '../../styles/MainChat/ChatLayout.tw';

// Components
import UserSelected from '../../components/MainChat/UserSelected';
import EnterMessage from './EnterMessage';

const SelfMessage = (props) => {
    return (
        <div className="flex flex-row justify-end px-16 py-1">
            <div className="max-w-xl bg-blue-600 shadow-md rounded-lg rounded-br-none p-5">
                <p className="font-normal text-white">Good morning. How are you today?</p>
                <div className="flex items-center justify-between">
                    <p className="text-sm font-medium text-gray-400 mt-1" >16 mins ago</p>

                </div>
            </div>
            <div style={{minHeight: '2.5rem', minWidth: '2.5rem'}} className="rounded-full ml-3 self-end shadow-md overflow-hidden">
                    <img className="h-10 w-10" src={require('../../assets/img/faces/face08.jpeg')} />
            </div>
        </div>
    );
}

const UserMessage = (props) => {
    return (
        <div className="flex flex-row justify-start px-16 py-1 pt-4">
            <div className="max-w-xl order-2 bg-white shadow-md rounded-lg rounded-bl-none p-5">
                <p className="font-normal leading-snug text-gray-800">Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At vero eos et accusam et justo duo dolores et ea rebum. Stet clita kasd gubergren, no sea takimata sanctus est Lorem ipsum dolor sit amet. Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At vero eos et accusam et justo duo dolores et ea rebum. Stet clita kasd gubergren, no sea takimata sanctus est Lorem ipsum dolor sit amet.</p>
                <div className="flex items-center justify-between">
                    <p className="text-sm font-medium text-gray-500 mt-1" >2 mins ago</p>
                </div>
            </div>
            <div style={{minHeight: '2.5rem', minWidth: '2.5rem'}} className="order-1 rounded-full mr-3 self-end shadow-md overflow-hidden">
                    <img className="h-10 w-10" src={require('../../assets/img/faces/face02.jpeg')} />
            </div>
        </div>
    );
}

class Chat extends React.Component {

    state = {
        userId: '',
        user: '',
        messageHistory: [
            {date: '2020-05-31', time: '14:23:05', text: 'Hey how are you doing', self: true},
            {date: '2020-05-31', time: '14:24:05', text: "I'm fine u?", self: false},
            {date: '2020-05-31', time: '14:25:05', text: 'Doing great', self: true},
            {date: '2020-05-31', time: '14:26:05', text: 'Tomorrow is the end of the world', self: true},
            {date: '2020-05-31', time: '14:27:05', text: "Well... I'm going for lunch", self: false},
            {date: '2020-05-31', time: '14:28:05', text: "Bye then!", self: true},
        ]
    };

    componentDidUpate() {
        if (this.props.user && !(this.state.user)) {
            this.setState({user: this.props.user})
        }
        console.log(this.props.user);
        // fetch user id


        // update user id

    }



    render() {
        if (!this.props.user) {
            return (
                <div className="w-full justify-center items-center flex py-64">
                    <h3 style={{color: '#BFBEC5'}} className="font-semibold text-4xl tracking-tight">Open a Chat</h3>
                    <svg style={{color: '#BFBEC5'}} className="h-24 w-24 fill-current" version="1.1" viewBox="0 0 32 32"  xmlns="http://www.w3.org/2000/svg" ><g id="Layer_1"/><g id="chat_x5F_alt_x5F_stroke"><g><g><path d="M25.977,12.213c-0.027,1.387-0.336,2.695-0.883,3.889C27.977,16.582,28,18.93,28,20     c0,0.992,0,4-3.996,4H20c-2.605,0-4.938,1.258-6.398,3.203C12.633,26.469,12,25.305,12,24v-4c0-0.791,0.141-1.414,0.359-1.928     C12.242,18.051,12.125,18,12,18H8.203c-0.129,0.619-0.199,1.281-0.199,2v4c0,4.418,3.578,8,7.996,8c0-2.211,1.789-4,4-4h4.004     C28,28,32,25.75,32,20C32,15.107,29.219,12.84,25.977,12.213z"/></g><path d="M16,4c1.492,0,4,0.52,4,4v4c0,1.309-0.633,2.471-1.602,3.201C16.938,13.26,14.609,12,12,12H8.004    C4,12,4,8.99,4,8c0-1.195,0-4,4.004-4H16 M16,0H8.004C4,0,0,2.125,0,8c0,5.75,4,8,8.004,8H12c2.211,0,4,1.793,4,4    c4.418,0,8.004-3.582,8.004-8V8C24.004,2.479,20,0,16,0L16,0z" /></g></g></svg>
                </div>
            );
        }
        console.log(this.props.user);
        return ( <>
            <UserSelected/>
            <ChatLayout>


                <div className="flex flex-row justify-end px-16 py-1 pt-4">
                    <div className="max-w-xl bg-blue-600 shadow-md rounded-lg rounded-br-none p-5">
                        <p className="text-left font-normal text-white">Sounds Great! Would you like to come with us? I've prepared some cupcakes and today is an awesome day, isn't it?</p>
                        <div className="flex items-center justify-between">
                            <p className="text-sm font-medium text-gray-400 mt-1" >1 mins ago</p>

                        </div>
                    </div>
                    <div style={{minHeight: '2.5rem', minWidth: '2.5rem'}} className="rounded-full ml-3 self-end shadow-md overflow-hidden">
                            <img className="h-10 w-10" src={require('../../assets/img/faces/face08.jpeg')} />
                    </div>
                </div>
                <div className="flex flex-row justify-end px-16 py-1">
                    <div className="max-w-xl bg-blue-600 shadow-md rounded-lg rounded-br-none p-5">
                        <p className="text-left font-normal text-white">Dont't forget!</p>
                        <div className="flex items-center justify-between">
                            <p className="text-sm font-medium text-gray-400 mt-1" >1 mins ago</p>

                        </div>
                    </div>
                    <div style={{minHeight: '2.5rem', minWidth: '2.5rem'}} className="rounded-full ml-3 self-end shadow-md overflow-hidden">
                            <img className="h-10 w-10" src={require('../../assets/img/faces/face08.jpeg')} />
                    </div>
                </div>
            </ChatLayout>
            <EnterMessage/>
        </>
        );
    }
}

export default Chat;
