import styled from 'styled-components';
import tw from 'twin.macro';


const StyledTopNotch = styled.div`
    & {
        ${tw`bg-indigo-800 p-2 text-indigo-100 leading-none lg:rounded-full flex lg:inline-flex items-center`}
        & .notch_pop {
            ${tw`rounded-full bg-indigo-500 uppercase px-2 py-1 text-xs font-bold mr-3`}
        }
        & .notch_info {
            ${tw`font-semibold mr-2 text-left flex-auto`}
        }
    }
`;



export default StyledTopNotch;
