import styled from 'styled-components';
import tw, {css} from 'twin.macro';
import React from 'react';


export const UserSelectWrapper = styled.div`${tw`px-5 py-6`}`;

export const UserFlex = styled.div`${tw`flex items-center px-5`}`;

export const ImageWrapper = styled.div`${tw`rounded-full shadow-lg overflow-hidden`}`;

export const UserText = styled.div`${tw`ml-3 text-lg text-gray-800 font-semibold`}`;

export const SettingsWrapper = styled.div`
    ${tw`border-l px-4 ml-auto`}
    border-color: #E9E8ED;
`;
