import React from 'react';
import {
    FocusChat,
    UserShowStyled,
    Dot_status,
    ImageWrapper,
    FullName,
    LastSeen
} from '../styles/Contacts.tw';

class Contacts extends React.Component {

    state = {userSelect: ''};

    componentDidUpdate() {
        if (this.props.user) {
            this.setState({userSelect: this.props.user});
        }
    }

    render = () => {
        return this.props.users.map((user) => {
            console.log(user);
            let _user = user;
            return (
                <FocusChat key={user.id} as="button" onClick={(e, user) => this.props.onToggleChat(e, user)} className={this.state.userSelect === user.id ? 'active' : ''}>
                    <UserShowStyled className={this.state.userSelect === user.id ? 'active' : ''}>
                        <div className="UserShowStyled__wraper_img">
                            <ImageWrapper>
                                <img className="h-10 w-10" src={require(`../assets/img/faces/face${user.id}.jpeg`)} />
                            </ImageWrapper>
                            <Dot_status/>
                        </div>

                        <div className="UserShowStyled__wrapper_info">
                            <FullName>{user.name}</FullName>
                            <LastSeen>{user.last_seen}</LastSeen>
                        </div>
                    </UserShowStyled>
                </FocusChat>
            )
        })
    }
} 

export default Contacts;
