import React from 'react';
import Container, { NewChat, SearchButton, UserShowStyled, FocusChat } from '../styles/s_app.tw';

class App extends React.Component {

    state = {
        userSelect: '',
        users: [
            {
                id: '01',
                name: 'Mary Doe',
                last_seen: '32 mins ago'
            },
            {
                id: '02',
                name: 'Dane Cool',
                last_seen: '1 hour ago'
            },
            {
                id: '03',
                name: 'Bob Man',
                last_seen: '6 mins ago'
            },
            {
                id: '04',
                name: 'Gab Mcley',
                last_seen: '2 hours ago'
            },
            {
                id: '05',
                name: 'Pep Koas',
                last_seen: '2 hours ago'
            },
            {
                id: '06',
                name: 'Dario Dar',
                last_seen: '2 hours ago'
            },
            {
                id: '07',
                name: 'Zoe Hue',
                last_seen: '2 hours ago'
            },
            {
                id: '08',
                name: 'Mea Ka',
                last_seen: '2 hours ago'
            },
        ]
    };

    toggleChat = (e, user) => {
        this.setState({userSelect: user.id});
    }

    render() {
        return (
            <Container>
                <div style={{borderColor: '#E6E5EA'}} className="w-2/6 inline-flex flex-col align-top border-r h-full">
                    {/******** BUTTON NEW CONVERSATION *****/}
                    <div className="w-full h-1/6 py-12 text-center">
                        <NewChat onClick={() => null} type="text">
                            New Conversation
                            <button onClick={(e) => alert(e)} className="absolute inset-y-0 left-0 flex items-center px-3 border-r">
                            <svg className="fill-current h-4 w-4 text-blue-700" id="Layer_1" style={{enableBackground: 'new 0 0 32 32'}} version="1.1" viewBox="0 0 32 32" width="32px" xmlns="http://www.w3.org/2000/svg" ><path d="M28,14H18V4c0-1.104-0.896-2-2-2s-2,0.896-2,2v10H4c-1.104,0-2,0.896-2,2s0.896,2,2,2h10v10c0,1.104,0.896,2,2,2  s2-0.896,2-2V18h10c1.104,0,2-0.896,2-2S29.104,14,28,14z"></path></svg>
                            </button>
                        </NewChat>
                    </div>
                    {/******** CHATS & SEARCH *****/}
                    <div className="pb-12 h-1/6">
                        <div className="mx-auto" style={{width: '14rem', paddingBottom: '3rem', marginBottom: '0.5rem'}}>
                            <h3 className="flex items-center relative text-3xl font-bold tracking-tight">Chats<svg type="button" style={{color: '#BFBEC5'}} className="inline-block cursor-pointer absolute right-0 h-6 w-6 fill-current" enableBackground="new 0 0 48 48" height="48px" id="Layer_3" version="1.1" viewBox="0 0 48 48" width="48px" xmlns="http://www.w3.org/2000/svg"><g><circle cx="5.5" cy="24" r="5.5"/><circle cx="42.499" cy="24" r="5.5"/><circle cx="24" cy="24" r="5.5"/></g></svg></h3>
                            <div className="mt-4 relative">
                                <span className="w-full overflow-hidden rounded-md" style={{backgroundColor: '#E7E6EB',position: 'relative', display: 'inline-block'}} ><SearchButton placeholder="Search here"/></span>
