import styled from 'styled-components';
import tw from 'twin.macro';


const StyledTopNotch = styled.div.attrs({
    className: "bg-indigo-900 text-center py-4 lg:px-4"
})`
    & {
       .layout {
        ${tw`bg-indigo-800 p-2 text-indigo-100 leading-none lg:rounded-full flex lg:inline-flex items-center`}
       }
    }
`;


export default StyledTopNotch;
