import React from 'react';

// MicroComponents
import NewChat from '../components/buttons/NewChat';
import ChatHead from '../components/ChatsHead';

// Components
import Contacts from '../components/Contacts';
import Chat from '../components/MainChat/Chat';

// Styles
import Container from '../styles/App/container.tw';


class App extends React.Component {

    state = {
        userSelect: '',
        users: [
            {
                id: '01',
                name: 'Mary Doe',
                last_seen: '32 mins ago'
            },
            {
                id: '02',
                name: 'Dane Cool',
                last_seen: '1 hour ago'
            },
            {
                id: '03',
                name: 'Bob Man',
                last_seen: '6 mins ago'
            },
            {
                id: '04',
                name: 'Gab Mcley',
                last_seen: '2 hours ago'
            },
            {
                id: '05',
                name: 'Pep Koas',
                last_seen: '2 hours ago'
            },
            {
                id: '06',
                name: 'Dario Dar',
                last_seen: '2 hours ago'
            },
            {
                id: '07',
                name: 'Zoe Hue',
                last_seen: '2 hours ago'
            },
            {
                id: '08',
                name: 'Mea Ka',
                last_seen: '2 hours ago'
            },
        ],
        filterChat: '',
    };

    toggleChat = (user) => {
        this.setState({userSelect: user.id});
    }

    selectUserChat = (users) => {
        for (let i=0; i < users.length; i++) {
            if (users[i].id === this.state.userSelect) {
                return users[i];
            }
        }
    }

    onSearch = (e) => {
        let value = e.target.value;
        this.setState({filterChat: value});
    }

    render() {
        return (
            <Container>
                <div style={{borderColor: '#E6E5EA'}} className="w-2/6 inline-flex flex-col align-top border-r h-screen">
                    {/******** BUTTON NEW CONVERSATION *****/}
                    <div className="py-6 text-center">
                        <NewChat onClick={(e) => alert(e)} />
                    </div>
                    {/******** CHATS & SEARCH *****/}
                    <div className="py-2">
                        <ChatHead onSearch={this.onSearch} />
                    </div>
                    {/******** CONTACTS SECTION *****/}
                    <div className="py-2 overflow-y-scroll">
                        <Contacts users={this.state.users} onToggleChat={this.toggleChat} user={this.state.userSelect} />
                    </div>
                </div>
                <div className="w-4/6 inline-flex flex-col">
                    {/*****  CHAT  *****/}
                    <Chat filter={this.state.filterChat} user={this.selectUserChat(this.state.users)} />
                </div>
            </Container>
        );
    }
}

export default App;
