import tw from 'twin.macro';
import styled, { createGlobalStyle } from 'styled-components';

export const GlobalStyle = createGlobalStyle`
    html {
        font-size: 120%;    
    }
`;

const Container = styled.main`
    ${tw`container mx-auto h-full flex-wrap items-center justify-center`}
    background-color: #F5F4F9;
`;

export const NewChat = styled.div`
    ${tw`bg-gray-100 shadow-lg outline-none focus:outline-none rounded-md px-5 pl-16 font-semibold py-2 inline-block relative`}
    top: 50%;
    transform: translateY(-50%);
    cursor: default;
    & button { 
        border-color: #E6E5EA;
        &:focus {
            z-index: 2;
            outline: none;
            box-shadow: 0 0 0 2px rgba(49, 130, 206, .2);
        }
    }
`;

export const SearchButton = styled.input`
    ${tw`px-5 py-2 appearance-none bg-gray-300 outline-none focus:outline-none`}
    background-color: #E7E6EB;
    &::placeholder {
        color: #A3A2A7;
    }
`;


export const FocusChat = styled.div`
    width: 18rem;
    ${tw`mx-auto flex items-center`}
    &.active {
        border: none !important;
        background-color: #FFFFFF !important;
        ${tw`rounded-md shadow-lg`}
    }
    &:focus {
        outline: none;
    }
`;

export const UserShowStyled = styled.div`
    width: 14rem;
    border-color: #E6E5EA;
    cursor: pointer;
    ${tw`flex mx-auto border-b py-3`}
    & .dot {
        height: 12px;
        width: 12px;
        display: inline-block;
        background-color: ${props => props.dot_color || "#49C967"};
        position: absolute;
        bottom: 0px;
        border: 2.5px solid white;
        ${tw`rounded-full inset-x-auto right-0`}
    }
    &.active {
        border: 0px solid;
    }
`;


export default Container;
